#pragma once

#define SERUM_VERSION_MAJOR 2  // X Digits
#define SERUM_VERSION_MINOR 0  // Max 2 Digits
#define SERUM_VERSION_PATCH 2  // Max 2 Digits

#define _SERUM_STR(x) #x
